func TestWriter_Write(t *testing.T) {
	for src, base64encodedExp := range writeCases {
		buffer := bytes.NewBuffer(nil)
		w := NewWriter(buffer)
		_, err := w.Write([]byte(src))
		if err != nil {
			t.Error("Encoding error: ", err)
		}
		w.Flush()
		exp, _ := base64.StdEncoding.DecodeString(base64encodedExp)
		dst := buffer.Bytes()
		if !bytes.Equal(dst, exp) {
			t.Errorf("Expected %s to generate %v, but was %v", src, exp, dst)
		}
		decoded, _ := Decode(dst)
		if string(decoded) != src {
			t.Error("Expected %s to be generated from %v ,but was %s", src, dst, string(decoded))
		}
	}
}
